sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
	if (isLoading||newValue === "") {
		return;
	}
	if(newValue!=oldValue){
		var emailAddress = new GlideAjax('GetEmailAddress');
		emailAddress.addParam('sysparm_name','getEmail');
		emailAddress.addParam('sysparm_userID',g_form.getValue('u_requested_for'));
		emailAddress.getXML(getEmailField);

		function getEmailField(response){
			var emailFromScriptInclude = response.responseXML.documentElement.getAttribute("answer");
			g_form.clearValue('u_string_1'); //"Requested for email" field
			g_form.setValue('u_string_1',emailFromScriptInclude);

		}}

		//Type appropriate comment here, and begin script below

	}
